export default {
  presets: [
    [
      '@docusaurus/preset-classic',
      {
        sitemap: {
          // highlight-start
          createSitemapItems: async ({
            defaultCreateSitemapItems,
            ...params
          }) => {
            const items = await defaultCreateSitemapItems(params);
            return items.filter((item) => !item.url.includes('/tags/'));
          },
          // highlight-end
        },
      },
    ],
  ],
};
